// MODULES //
var Float64Array = require( '@stdlib/array/float64' );
var sqrt = require( '@stdlib/math/base/special/sqrt' );
var format = require( '@stdlib/string/format' );
var dgemm = require('@stdlib/blas/base/dgemm');

// MAIN //

/**
* Computes the resultant forces of omnidirectional wheeled robots given the applied wheel forces.
*
* @param {PositiveInteger} wheelbase - distance of wheel from the center of the robot (meters)
* @param {number} f1 - applied force on wheel 1 (Newton)
* @param {number} f2 - applied force on wheel 2 (Newton)
* @param {number} f3 - applied force on wheel 3 (Newton)
*
* @example
* var omni_directional = require( 'robotwheel/dforce2wforce/omnidirectional' );
*
* var z = omni_directional( 2.0, 5.0, 5.0, 5.0 );
* // z => <Float64Array>[ 5.0, ~-2.886, ~-2.886 ]
*/
function omni_directional(wheelbase, f1, f2, f3 ) {
    var wheel_force_vector;
    var transformation_matrix;
    var directional_force_vector;
    var l;

    if ( wheelbase <= 0 ) {
        throw new RangeError( format( 'invalid argument. First argument must be a positive integer. Value: `%d`.', wheelbase ) );
    }

    l = wheelbase / 2;

    wheel_force_vector = new Float64Array([f1, f2, f3]);

    directional_force_vector = new Float64Array( 3 );

    transformation_matrix = new Float64Array([
        0, ( 2/3 ), ( 1/( 3*l ) ),
        -(sqrt(3)/3), -(1/3), ( 1/( 3*l ) ),
        (sqrt(3)/3), -(1/3), ( 1/( 3*l ) )
    ]);

    dgemm('row-major', 'no-transpose', 'no-transpose',
        3, 1, 3,
        1,
        transformation_matrix, 3,
        wheel_force_vector, 1,
        0,
        directional_force_vector, 1);

    return directional_force_vector;
}


// EXPORTS //

module.exports = omni_directional;
