/**
* Computes the wheel velocities of differential wheeled robots given the applied forces in all directions.
*
* @module robotwheel/force2point/differential
*
* @example
* var differential = require( 'robotwheel/force2point/differential' );
*
* var z = differential( 10.0, 20.0, 10.0, 5.0, 10.0, 15.0 );
* // z => <Float64Array>[ 2.0, 8.0 ]
*/
// MODULES //

var join = require( 'path' ).join;
var tryRequire = require( '@stdlib/utils/try-require' );
var isError = require( '@stdlib/assert/is-error' );
var main = require( './main.js' );


// MAIN //

var tmp = tryRequire( join( __dirname, './native.js' ) );
if ( isError( tmp ) ) {
	differential = main;
} else {
	differential = tmp;
}

module.exports = differential;