/**
* Interface describing `mecanum-wheeled`.
*/
interface Routine {
    /**
    * Computes the wheel velocities of mecanum-wheeled robots given final orientation of the robot.
    *
    * @param radius - radius of the wheel
    * @param wheelbase - distance between the front and rear wheels
    * @param trackwidth - distance between the left and right wheels
    * @param x0 - initial x position in meters
    * @param y0 - initial y position in meters
    * @param psi0 - initial orientation in degrees
    * @param x1 - final x position in meters
    * @param y1 - final y position in meters
    * @param psi1 - final orientation in degrees
    * @param time - time in seconds
    * @throws first argument must be a positive integer
    * @throws second argument must be a positive integer
    * @throws third argument must be a positive integer
    * @returns wheel velocities
    *
    * @example
    *
    * var z = mecanum_wheeled( 0.1, 1.0, 0.8, 0.0, 0.0, 0.0, 1.0, 0.5, 0.0, 1.0 );
    * // z => <Float64Array>[ 5.0, 15.0, 5.0, 15.0 ]
    */
    ( radius: number, wheelbase: number, x0: number, y0: number, psi0: number, x1: number, y1: number, psi1: number, time: number ): Float64Array;
}

/**
* Computes the wheel velocities of mecanum-wheeled robots given final orientation of the robot.
*
* @param radius - radius of the wheel
* @param wheelbase - distance between two wheels
* @param trackwidth - distance between the left and right wheels
* @param x0 - initial x position in meters
* @param y0 - initial y position in meters
* @param psi0 - initial orientation in degrees
* @param x1 - final x position in meters
* @param y1 - final y position in meters
* @param psi1 - final orientation in degrees
* @param time - time in seconds
* @throws first argument must be a positive integer
* @throws second argument must be a positive integer
* @throws third argument must be a positive integer
* @returns wheel velocities
*
* @example
*
*var z = mecanum_wheeled( 0.1, 1.0, 0.8, 0.0, 0.0, 0.0, 1.0, 0.5, 0.0, 1.0 );
* // z => <Float64Array>[ 5.0, 15.0, 5.0, 15.0 ]
*/
declare var mecanum_wheeled: Routine;

// EXPORTS //

export = mecanum_wheeled;